mapper.select:
select exhibition.*
from exhibition
where name like CONCAT ('%', #{keyword,jdbcType=VARCHAR},'%');

select tag.*
from (
    select * from exhibition_tag as et where et.ex_id=#{ex_id}
) as t left join tag on t.tag_id=tag.id;

select id,name,venue_name,organizer,begin_date,end_date,is_checked
from user_ex_relation u join exhibition e
on u.user_id = #{keyword}
and u.ex_id=e.id;
